import { makeAutoObservable } from "mobx"
import { http } from '@/utils'

class PromptsStore {
  token = ''
  constructor() {
    makeAutoObservable(this)
  }
  // 登录
  getPrompts = async ({ task_num , task_detail }) => 
  {
    const res = await http.post('https://tritium.work:5000/upload_task', 
    {
      task_num,
      task_detail,
    })
    //存入token
    console.log(res.data)
    this.token = res.data.token
  }
}
export default PromptsStore
