import Card from "antd/es/card/Card"
import logo from '@/assets/Ju.png'
import JUJU from '@/assets/JUJU.jpg'
import './index.scss'
import { Form, Input, Button, Checkbox } from 'antd'
import { Link } from "react-router-dom"
import { useStore } from "@/store/login.Store"
function Login()
{
    const{loginstore} = useStore()
    function onFinish(values)
    {
        console.log(values)
        loginstore.getToken({
            mobile:values.mobile,
            code:values.code
        })
    }
    function onfinishFailed(erroinfo)
    {
        console.log('Failed:',erroinfo)
    }
    return(
        <div className="login">
            <Card className="login-container">
                <img className="login-logo" src={logo} alt=""/>
                <img className="login-background" src={JUJU} alt=""/>
                
                
                <Form
                    onFinish={onFinish}
                    onFinishFailed={onfinishFailed}
                >
                    <Form.Item
                        //数据校验
                        //不知道为什么只校验一次
                        name="mobile"
                        rules=
                        {[
                            {
                                required : true,
                                message:"请输入原神uid",
                            },
                        ]}
                        
                    >
                        <Input size="large" placeholder="请输入手机号" />
                        
                    </Form.Item>
                    <Form.Item
                        name="code"
                        rules=
                        {[
                            {
                                required : true,
                                message:"请输入原神密码",
                            },
                        ]}
                        
                    >
                        <Input size="large" placeholder="请输入验证码" />
                        
                    </Form.Item>
                    <Form.Item
                        name="remember"
                        valuePropName="checked"
                        //如果不调用此行函数，对勾的输出结果将是undefined
                    >
                        <Checkbox className="login-checkbox-label">
                        我已阅读并同意「用户协议」和「隐私条款」
                        </Checkbox>
                    </Form.Item>
                    <Link to="/Feiju">
                        <Button className="ReadTxt" type="text">
                        阅读「用户协议」和「隐私条款」
                        </Button>
                    </Link>
                    <Form.Item>
                        <Button type="primary" htmlType="submit" size="large" block>
                        我承认原神是3A大作
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
                
        </div>
        
    )
}
export default Login
