//React组件
import { useState } from 'react';
//
//Tags输入所属
import { AutoComplete, Input } from 'antd';
//
//Antd杂项
import { FileOutlined, UserOutlined , DesktopOutlined , TeamOutlined , DropboxOutlined ,SearchOutlined } from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
//图片资源

//
//scss文件导入 操控页面具体布局
import './index.scss';
//
//--------分割线--------//
//Tags输入所属
const TextArea = {Input};
//
const { Content, Footer, Sider } = Layout;
const onChange = (value, selectedOptions) => 
{
  console.log(value, selectedOptions);
};
//储存搜索数据
const onSearch = (value) => console.log(value);
//
function getItem(label, key, icon, children) 
{
  return {
    key,
    icon,
    children,
    label,
  };
}
//定义了一个items组用于储存侧栏物品
const items = [
  getItem('欢迎来到本站', '1' ,<DesktopOutlined/> ),
  getItem('使用AI', 'sub1', <UserOutlined />,  
  [
    getItem('AI绘画', 'sub2',null,[
      getItem('AI虚拟形象','7'),
      getItem('AIDrawer','8'),
    ]),
    getItem('AI建模','5'),
    getItem('AIChat','6'),
    getItem('AI音声合成','sub3',null,[
      getItem('模型训练','3'),
      getItem('声线合成','4')
    
    ]),
  ]),
  getItem('成为贡献者', '5',<DropboxOutlined />),
];
//
const Laypage = () => 
{
  const [collapsed, setCollapsed] = useState(false);
  //collapsible可以设定侧栏是否可以折叠
  return (
    <Layout
      style=
      {{
        minHeight: '100vh',
      }}

    >
      <Sider className='Sider' collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)} theme='dark'>
        <Menu className='FunctionMenu' theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items}>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Content
          style=
          {{
            margin: '0 20px',
          }}
        >
          <Breadcrumb className='BreadCrumb'>
            <Breadcrumb.Item>使用AI</Breadcrumb.Item>
            <Breadcrumb.Item>AI绘画</Breadcrumb.Item>
          </Breadcrumb>
          <TextArea
            placeholder="input here"
            className="custom"
            style=
            {{
              height: 50,
            }}
            //onKeyPress={handleKeyPress}
          ></TextArea>
        </Content>
        <Footer
          style=
          {{
            textAlign: 'center',
          }}
        >
          TBD ©2023 Created by Yuanshin
        </Footer>
      </Layout>
    </Layout>
  );
};
export default Laypage;
