//Assest head
import Card from "antd/es/card/Card"
import Logo from '@/assets/Logo001.png'
//Assest tail
import './index.scss'
import { Form, Input, Button, Checkbox } from 'antd'
//
import { Link } from "react-router-dom"
//
import { useStore } from "@/store"
//
import { useNavigate } from "react-router-dom"
//提示信息模块
import { message } from "antd"
//头像模块
import { Space } from "antd"
import { Avatar } from "antd" 
import { UserOutlined } from '@ant-design/icons'
import Avt001 from '@/assets/Avatar/001.png'
//
function Login()
{
    const navigate = useNavigate()
    //提示信息模块head
    const [messageApi, contextHolder] = message.useMessage();
    const success = () =>
    {
        messageApi.open({
            type: 'success',
            content: '登陆成功',
            className: 'custom-class',
            style: 
            {
              marginTop: '20vh',
            },
          });
        
    }
    //提示信息模块tail
    const { loginStore } = useStore()
    async function onFinish(values)
    {
        console.log(values)
        await loginStore.getToken({
            mobile:values.mobile,
            code:values.code
        })
        navigate('/Layout' , {replace : true})     
    }
    function onfinishFailed(erroinfo)
    {
        console.log('Failed:',erroinfo)
    }
    return(
        <div className="login">
            <Card className="login-container">
                <img className="login-Logo" src={Logo} alt=""/>
                <Avatar className="login-avatar" size={128} src={Avt001}>
                </Avatar>          
                <Form
                    onFinish={onFinish}
                    onFinishFailed={onfinishFailed}
                >
                    <Form.Item
                        //数据校验
                        //不知道为什么只校验一次
                        name="mobile"
                        initialValue={'18560062721'}
                        rules=
                        {[
                            {
                                required : true,
                                message:"请输入11位手机号码",
                            },
                        ]}
                        
                    >
                        <Input size="large" placeholder="请输入手机号" />
                        
                    </Form.Item>
                    <Form.Item
                        name="code"
                        initialValue={'246810'}
                        rules=
                        {[
                            {
                                required : true,
                                message:"请输入验证码",
                            },
                        ]}
                        
                    >
                        <Input size="large" placeholder="请输入验证码" />
                        
                    </Form.Item>
                    <Form.Item
                        name="remember"
                        valuePropName="checked"
                        //如果不调用此行函数，对勾的输出结果将是undefined
                    >
                        <Checkbox className="login-checkbox-label">
                        我已阅读并同意「用户协议」和「隐私条款」
                        </Checkbox>
                    </Form.Item>
                    <Link to="/Feiju">
                        <Button className="ReadTxt" type="text" size="small">
                        阅读「用户协议」和「隐私条款」
                        </Button>
                    </Link>
                    <Form.Item>
                        {contextHolder}
                        <Button type="primary" htmlType="submit" size="large" block onClick={success}>
                        登录
                        </Button>
                    </Form.Item>
                </Form>
            </Card>
                
        </div>
        
    )
}
export default Login
