import { FileOutlined, UserOutlined , DesktopOutlined , TeamOutlined , DropboxOutlined ,SearchOutlined } from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
import { useState } from 'react';
import { Select, Space , Button , Tooltip ,Input , Upload} from 'antd';
import './index.scss';
import Wallpaper001 from '@/assets/Wallpaper001.JPG';
import { get } from 'mobx';
import { UploadOutlined } from '@ant-design/icons';


const props = 
{
  action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
  onChange({ file, fileList }) {
    if (file.status !== 'uploading') {
      console.log(file, fileList);
    }
  },
  defaultFileList: [
    {
      uid: '1',
      name: 'xxx.png',
      status: 'uploading',
      url: 'http://www.baidu.com/xxx.png',
      percent: 33,
    },
    {
      uid: '2',
      name: 'yyy.png',
      status: 'done',
      url: 'http://www.baidu.com/yyy.png',
    },
    {
      uid: '3',
      name: 'zzz.png',
      status: 'error',
      response: 'Server Error 500',
      // custom error message to show
      url: 'http://www.baidu.com/zzz.png',
    },
  ],
};


//分割线，下线前记得整理import
const { Content, Footer, Sider } = Layout;
const { Search } = Input;
const onChange = (value, selectedOptions) => 
{
  console.log(value, selectedOptions);
};
//储存搜索数据
const onSearch = (value) => console.log(value);
//
function getItem(label, key, icon, children) 
{
  return {
    key,
    icon,
    children,
    label,
  };
}
//定义了一个items组用于储存侧栏物品
const items = [
  getItem('欢迎来到本站', '1' ,<DesktopOutlined/> ),
  getItem('使用AI', 'sub1', <UserOutlined />,  
  [
    getItem('AI绘画', 'sub3',null,[
      getItem('AI虚拟形象','7')
    ]),
    getItem('AI建模','5'),
    getItem('AiChat','6'),
    getItem('AI音声合成','sub2',null,[
      getItem('模型训练','3'),
      getItem('声线合成','4')
    
    ]),
  ]),
  getItem('成为贡献者', '5',<DropboxOutlined />),
];
const Laypage = () => {
  const [collapsed, setCollapsed] = useState(false);
  const 
  {
    token: { colorBgContainer },
  } = theme.useToken();
  //collapsible可以设定侧栏是否可以折叠
  return (
    <Layout
      style=
      {{
        minHeight: '100vh',
      }}

    >
      <Sider className='Sider' collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)} theme='light'>
        <Menu className='FunctionMenu' theme="light" defaultSelectedKeys={['1']} mode="inline" items={items}>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Content
          style=
          {{
            margin: '0 20px',
          }}
        >
          <Breadcrumb className='BreadCrumb'>
            <Breadcrumb.Item>使用AI</Breadcrumb.Item>
            <Breadcrumb.Item>AI绘画</Breadcrumb.Item>
          </Breadcrumb>
          <img className='ImgShow' src={Wallpaper001}>
          </img>
          <Upload {...props} className='Upload'>
            <Button icon={<UploadOutlined />}>Upload</Button>
          </Upload>
        </Content>
        <Footer
          style={{
            textAlign: 'center',
          }}
        >
          TBD ©2023 Created by Yuanshin
        </Footer>
      </Layout>
    </Layout>
  );
};
export default Laypage;
