import { FileOutlined, UserOutlined , DesktopOutlined , TeamOutlined} from '@ant-design/icons';
import { Breadcrumb, Layout, Menu, theme } from 'antd';
import { useState } from 'react';
import { Select, Space } from 'antd';
import { Cascader } from 'antd';
const { Header, Content, Footer, Sider } = Layout;
const onChange = (value, selectedOptions) => 
{
  console.log(value, selectedOptions);
};
const filter = (inputValue, path) =>
  path.some((option) => option.label.toLowerCase().indexOf(inputValue.toLowerCase()) > -1);
function getItem(label, key, icon, children) 
{
  return {
    key,
    icon,
    children,
    label,
  };
}
//定义了一个items组用于储存侧栏物品
const items = [
  getItem('欢迎来到本站', '1' ,<DesktopOutlined/> ),
  getItem('使用AI', 'sub1', <UserOutlined />,  
  [
    getItem('AI绘画', '2'),
    getItem('AI音声合成','sub2',null,[
      getItem('模型训练','3'),
      getItem('声线合成','4')
    ]),
  ]),
  getItem('成为贡献者', '5'),
];
const Laypage = () => {
  const [collapsed, setCollapsed] = useState(false);
  const 
  {
    token: { colorBgContainer },
  } = theme.useToken();
  //collapsible可以设定侧栏是否可以折叠
  return (
    <Layout
      style=
      {{
        minHeight: '100vh',
      }}

    >
      <Sider collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)}>
        <div
          style=
          {{
            height: 20,
            margin: 0,
            background: 'rgba(255, 255, 255, 0.2)',
          }}
        />
          <Cascader
          onChange={onChange}
          placeholder="搜索功能"
          showSearch=
          {{
            filter,
          }}
          onSearch={(value) => console.log(value)}
          style=
          {{
            margin: '5px 8px',
          }}
          />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" items={items} />
      </Sider>
      <Layout className="site-layout">
        <Header
          style={{
            padding: 0,
            background: colorBgContainer,
          }}
        />
        <Content
          style={{
            margin: '0 16px',
          }}
        >
          <Breadcrumb
            style={{
              margin: '16px 0',
            }}
          >
            <Breadcrumb.Item>使用AI</Breadcrumb.Item>
            <Breadcrumb.Item>AI绘画</Breadcrumb.Item>
          </Breadcrumb>
          <Select
            mode="multiple"
            allowClear
            style=
            {{
              width: '100%',
            }}
            placeholder="请输入绘画标签"
            defaultValue={['白发', '黑发']}
            //onChange={handleChange}
            //options={options}
          />
          <div
            style={{
              padding: 24,
              minHeight: 360,
              background: colorBgContainer,
            }}
          >
            你不会自己画？
          </div>
        </Content>
        <Footer
          style={{
            textAlign: 'center',
          }}
        >
          TBD ©2023 Created by Yuanshin
        </Footer>
      </Layout>
    </Layout>
  );
};
export default Laypage;
