//React组件
import { useState } from 'react';
import { useStore } from "@/store";
//
//Tags输入所属
//
//Antd杂项
import { Breadcrumb } from 'antd';
import { Layout } from 'antd';
import { Menu } from 'antd';
import { Button } from 'antd';
import { Form } from 'antd';
import { Space } from 'antd';//拉开组件间隔
//import { Radio } from 'antd'
import {Input} from 'antd';
import { FileOutlined, UserOutlined , DesktopOutlined , TeamOutlined , DropboxOutlined ,SearchOutlined } from '@ant-design/icons';

//图片资源

//
//scss文件导入 操控页面具体布局
import './index.scss';
//
//--------分割线--------//
//Tags输入所属
//
const { TextArea } = Input;
const { Content, Footer, Sider } = Layout;
const onChange = (value, selectedOptions) => 
{
  console.log(value, selectedOptions);
};
//储存搜索数据
const onSearch = (value) => console.log(value);
//
function getItem(label, key, icon, children) 
{
  return {
    key,
    icon,
    children,
    label,
  };
}
//定义了一个items组用于储存侧栏物品
const items = [
  getItem('欢迎来到本站', '1' ,<DesktopOutlined/> ),
  getItem('使用AI', 'sub1', <UserOutlined />,  
  [
    getItem('AI绘画', 'sub2',null,[
      getItem('AI虚拟形象','7'),
      getItem('AIDrawer','8'),
    ]),
    getItem('AI建模','5'),
    getItem('AIChat','6'),
    getItem('AI音声合成','sub3',null,[
      getItem('模型训练','3'),
      getItem('声线合成','4')
    
    ]),
  ]),
  getItem('成为贡献者', '5',<DropboxOutlined />),
];
//
const Laypage = () => 
{
  const [collapsed, setCollapsed] = useState(false);
  //collapsible可以设定侧栏是否可以折叠
  const {promptsStore} = useStore()
  async function onFinish(values)
  {
    await promptsStore.getPrompts({
      username:values.prompts,
      password:values.Nprompts,
    });  
  }
  return (
    <Layout
      style=
      {{
        minHeight: '100vh',
      }}

    >
      <Sider className='Sider' collapsible collapsed={collapsed} onCollapse={(value) => setCollapsed(value)}>
        <Menu className='FunctionMenu' defaultSelectedKeys={['1']} mode="inline" items={items} theme='dark'>
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Content
          style=
          {{
            margin: '0 20px',
          }}
        >
          <Space
            direction="vertical"
            size="middle"
            style=
            {{
              display: 'flex',
            }}
          >
            <Breadcrumb className='BreadCrumb'>
              <Breadcrumb.Item>使用AI</Breadcrumb.Item>
              <Breadcrumb.Item>AI绘画</Breadcrumb.Item>
            </Breadcrumb>
            <br />
            <br />
            <Form className='Tags' onFinish={onFinish}>
              <Form.Item name="Prompts">
              <TextArea rows={6} placeholder="请输入AI应该采用的绘画元素" className='GoodTags'/>
              </Form.Item>
              <Form.Item name="NPrompts">
              <TextArea rows={6} placeholder="请输入AI需要避免的绘画元素" className='BadTags'/>
              </Form.Item>
            </Form>
          </Space>
          <Button type="primary" className='GenerateButton'>
            生成图片
          </Button>
          

        </Content>
        <Footer
          style=
          {{
            textAlign: 'center',
          }}
        >
          TBD ©2023 Created by Yuanshin
        </Footer>
      </Layout>
    </Layout>
  );
};
export default Laypage;
